INSERT INTO console (console, consolePrice, platform, itemDesc) VALUES 
        ("PlayStation 1", 32.91, "Playstation", "The PlayStation (abbreviated as PS, commonly known as the PS1 or its codename PSX) is a home video game console developed and marketed by Sony Computer Entertainment."),
